import React, { useState } from 'react';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './ecommerce.css';

const AddProduct = () => {
  const [productName, setProductName] = useState('');
  const [barcode, setBarcode] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [category, setCategory] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/products', {
        name: productName,
        barcode,
        description,
        price,
        quantity,
        category,
      });
      
      // Redirect to Dashboard with success message as state
      navigate('/dashboard', { state: { message: 'Product added successfully!' } });
    } catch (error) {
      console.error('Error adding product:', error);
      // Optionally, handle error here
      navigate('/dashboard', { state: { message: 'Failed to add product.' } });
    }
  };

  return (
    <Container className="add-product-container">
      <h2>Add Product</h2>
      <Form onSubmit={handleSubmit}>
        {/* Form fields here */}
        <Button variant="primary" type="submit">Add Product</Button>
      </Form>
    </Container>
  );
};

export default AddProduct;
