import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ecommerce.css';

const EditProduct = () => {
  const [product, setProduct] = useState({
    barcode: '',
    description: '',
    price: '',
    quantity: '',
    category: ''
  });
  const [loading, setLoading] = useState(true); // Loading state to show when data is being fetched
  const { id } = useParams(); // Get product ID from URL params
  const navigate = useNavigate();

  // Fetch product data when the component mounts or when the ID changes
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/products/${id}`); // Fetch product by ID
        console.log(response.data); // Log the fetched data to ensure it's correct
        setProduct(response.data); // Set fetched product data to state
        setLoading(false); // Stop loading once data is fetched
      } catch (error) {
        console.error('Error fetching product data:', error);
        setLoading(false); // Stop loading even if there's an error
      }
    };
    fetchProduct();
  }, [id]); // Re-fetch when the ID changes (for example, on route change)

  // Handle changes in the form input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value // Update the specific field
    }));
  };

  // Handle form submission to update the product
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/products/${id}`, product); // Update product using PUT
      alert('Product updated successfully!');
      navigate('/view-products'); // Redirect after successful update
    } catch (error) {
      console.error('Error updating product:', error);
      alert('Failed to update product.');
    }
  };

  if (loading) {
    return <p>Loading product data...</p>; // Show loading message while data is being fetched
  }

  return (
    <div className="container">
      <h2>Edit Product</h2>
      <form onSubmit={handleSubmit}>
        {/* Barcode Field */}
        <div className="form-group">
          <input
            type="text"
            name="barcode"
            value={product.barcode}
            onChange={handleChange}
            id="barcode"
            placeholder=" " // Placeholder for floating label effect
            required
            className="input-field"
          />
          <label htmlFor="barcode">Barcode</label>
        </div>

        {/* Description Field */}
        <div className="form-group">
          <textarea
            name="description"
            value={product.description}
            onChange={handleChange}
            id="description"
            placeholder=" " // Placeholder for floating label effect
            required
            className="input-field"
          />
          <label htmlFor="description">Description</label>
        </div>

        {/* Price Field */}
        <div className="form-group">
          <input
            type="number"
            name="price"
            value={product.price}
            onChange={handleChange}
            id="price"
            placeholder=" " // Placeholder for floating label effect
            required
            className="input-field"
          />
          <label htmlFor="price">Price</label>
        </div>

        {/* Quantity Field */}
        <div className="form-group">
          <input
            type="number"
            name="quantity"
            value={product.quantity}
            onChange={handleChange}
            id="quantity"
            placeholder=" " // Placeholder for floating label effect
            required
            className="input-field"
          />
          <label htmlFor="quantity">Quantity</label>
        </div>

        {/* Category Field */}
        <div className="form-group">
          <input
            type="text"
            name="category"
            value={product.category}
            onChange={handleChange}
            id="category"
            placeholder=" " // Placeholder for floating label effect
            required
            className="input-field"
          />
          <label htmlFor="category">Category</label>
        </div>

        <button type="submit">Update Product</button>
      </form>
    </div>
  );
};

export default EditProduct;
