import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Toast } from 'react-bootstrap'; // Added Toast for notifications
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './ecommerce.css';

const AddProduct = () => {
  const [productName, setProductName] = useState('');
  const [barcode, setBarcode] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [category, setCategory] = useState('');
  const [showNotification, setShowNotification] = useState(false); // State for notification visibility
  const [notificationMessage, setNotificationMessage] = useState(''); // State for the notification message
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/products', {
        name: productName,
        barcode,
        description,
        price,
        quantity,
        category,
      });
      
      // Set notification message and show the notification
      setNotificationMessage('Product added successfully!');
      setShowNotification(true);

      // Hide the notification after 3 seconds
      setTimeout(() => setShowNotification(false), 3000);

      // Redirect to ViewProduct after successful submission
      navigate('/view-products'); // Adjust the route as per your application
    } catch (error) {
      console.error('Error adding product:', error);
      setNotificationMessage('Failed to add product.');
      setShowNotification(true);

      // Hide the notification after 3 seconds
      setTimeout(() => setShowNotification(false), 3000);
    }
  };

  return (
    <Container className="add-product-container">
      <div className="floating-form">
        <h2>Add Product</h2>
        <Form onSubmit={handleSubmit}>
          <Row className="mb-3">
            {/* Product Name Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="productName">
                <Form.Label>Product Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter product name"
                  value={productName}
                  onChange={(e) => setProductName(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            {/* Barcode Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="barcode">
                <Form.Label>Barcode</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter barcode"
                  value={barcode}
                  onChange={(e) => setBarcode(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            {/* Description Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="description">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  as="textarea"
                  placeholder="Enter description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            {/* Price Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="price">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter price"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            {/* Quantity Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="quantity">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter quantity"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            {/* Category Field */}
            <Col xs={12} sm={6} lg={4}>
              <Form.Group controlId="category">
                <Form.Label>Category</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter category"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Button variant="primary" type="submit" className="submit-button">
            Add Product
          </Button>
        </Form>

        {/* Notification Toast */}
        {showNotification && (
          <Toast className="notification-toast" bg="success" onClose={() => setShowNotification(false)} autohide delay={3000}>
            <Toast.Body>{notificationMessage}</Toast.Body>
          </Toast>
        )}
      </div>
    </Container>
  );
};

export default AddProduct;
