import React, { useEffect, useState } from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ecommerce.css';

const ProductDetails = () => {
  const { id } = useParams();  // Get product ID from URL params
  const [product, setProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/products/${id}`);
        if (response.data) {
          setProduct(response.data);
        }
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    fetchProduct();
  }, [id]);

  const handleBack = () => {
    navigate('/view-products');  // Navigate back to product list
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct({
      ...product,
      [name]: value
    });
  };

  const handleSave = async () => {
    try {
      await axios.put(`/api/products/${id}`, product);  // Update product details in the API
      alert('Product details updated successfully');
    } catch (error) {
      console.error('Error updating product details:', error);
    }
  };

  return (
    <Container className="product-details-container">
      {product ? (
        <div>
          <h2>Product Details</h2>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={product.name}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="productBarcode">
              <Form.Label>Barcode</Form.Label>
              <Form.Control
                type="text"
                name="barcode"
                value={product.barcode}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="productDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="description"
                value={product.description}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="productPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={product.price}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="productQuantity">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                name="quantity"
                value={product.quantity}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="productCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                type="text"
                name="category"
                value={product.category}
                onChange={handleChange}
              />
            </Form.Group>

            {/* Save and Back Buttons */}
            <Button variant="primary" onClick={handleSave} className="me-2">
              Save Changes
            </Button>
            <Button variant="secondary" onClick={handleBack}>
              Back to Product List
            </Button>
          </Form>
        </div>
      ) : (
        <p>Loading product details...</p>
      )}
    </Container>
  );
};

export default ProductDetails;
